
def tonelli_shanks(a, p):
    """
    Tonelli-Shanks algorithm for finding the square root of a modulo a prime p.
    Returns the smaller of the two roots.
    """
    if legendre_symbol(a, p) != 1:
        return None

    # Step 1: Write p - 1 as q * 2^s, where q is odd
    q, s = p - 1, 0
    while q % 2 == 0:
        q //= 2
        s += 1

    # Step 2: Find a non-quadratic residue of p
    z = 2
    while legendre_symbol(z, p) != -1:
        z += 1

    # Step 3: Initialize variables
    m = s
    c = pow(z, q, p)
    t = pow(a, q, p)
    r = pow(a, (q + 1) // 2, p)

    # Step 4: Main loop
    while t != 1:
        i, tt = 0, t
        while tt != 1 and i < m:
            tt = (tt * tt) % p
            i += 1

        if i == 0:
            return r

        b = pow(c, pow(2, m - i - 1, p - 1), p)
        r = (r * b) % p
        t = (t * b * b) % p
        c = (b * b) % p
        m = i

    return r

def legendre_symbol(a, p):
    """
    Legendre symbol. Returns 1 if a is a quadratic residue modulo p, -1 otherwise.
    """
    ls = pow(a, (p - 1) // 2, p)
    if ls == p - 1:
        return -1
    return ls

